# how may elements are in each of the following lists?
first_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']
second_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']

print(f"The first list contains {len(first_list)} elments and the second list consists out of {len(second_list)} elements.\n")

# which and how many elements are common to the two lists above?
common_elements = first_list and second_list
print(f"There are {len(common_elements)} elements common to both lists, with the elements beeing: {", ".join(common_elements)}.\n")

# how often does the character 'a' occur in each of the lists above?
def num2times(num: int):
    if num == 1:
        return "once"
    elif num == 2:
        return "twice"
    return f"{num} times"

print(f"The character a occurs {num2times(str(first_list).count("a"))} in the first and {num2times(str(second_list).count("a"))} in the second list.\n")