# how may elements are in each of the following lists?
first_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']
second_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']
print(f"In the list: first_list are exactly {len(first_list)} elements, while the second list: second_list contains {len(second_list)} elements.\n")

# which and how many elements are common to the two lists above? 
common_elements = set(first_list) & set(second_list)  #Finding common elements using set 
common_count = len(common_elements)  # Counting the found elements 
print(f"The two lists have {common_count} common elements: {common_elements}\n")


# how often does the character 'a' occur in each of the lists above?
def find_character(list):# function to count the amount of specified characters out of a list of strings 
   match = 0  
   for element in list:
        match += element.count('a')  # Count 'a' in each element
   return match

print(f"The list: first_list contains {find_character(first_list)} a's, while the list: second_list contains {find_character(second_list)}a's.\n")
