def get_qoutient_and_remainder_int(q1, q2):
    quotient = q1 // q2
    remainder = q1 % q2
    return quotient, remainder

# what are the quotient and remainder after (integer) division of 2711274328912 by 23369245575
quotient, remainder = get_qoutient_and_remainder_int(2711274328912,23369245575 )
print(Fore.CYAN + f'quotient: {quotient} remainder: {remainder}')

