print(Fore.CYAN + f'quotient: {quotient} remainder: {remainder}')
# assume the variable below is a temperature in degrees Fahrenheit. Print the same temperature converted to °C.
def fahrenheit_to_celsius(temp):
    return (temp-32) / 1.8

t_fahrenheit = 451
t_celsius = fahrenheit_to_celsius(t_fahrenheit)
print(Fore.GREEN +  f'temperature in celsius: {t_celsius} °C')
# hier caste ich nicht mit int(), sondern nutze round damit 232.777 auf 233 gerundet wird und nicht 232
print(Fore.GREEN +  f'temperature rounded in celsius: {round(t_celsius)} °C')


# determine how many more '(' than ')' symbols occur in the following string:
def count_paranthesis_diff(input):
    count_open = input.count("(")
    count_closed = input.count(")")
    if(count_open> count_closed):
        difference = count_open - count_closed
        return f'there are {difference} more open paranthesis then closed ones'
    elif( count_closed > count_open):  
        difference = count_closed - count_open
        return f'there are {difference} more closed paranthesis then open ones'
    else:
        return "the amount of paranthesis are the same"

