second_list = ['ybk', 'zcg', 'dhw', 'gua', 'mqh', 'yel', 'fpk', 'imk', 'nkt', 'tpl', 'guc', 'ozk', 'dip', 'thn', 'pxc', 'obk', 'orl', 'rdc', 'vto', 'wen', 'tbw', 'itj', 'qfb', 'ymd', 'txb', 'vgw', 'wgd', 'tcg', 'ezm', 'gnt', 'ehf', 'pfo', 'alf']
print(Fore.WHITE + f'length of first list: {len(first_list)}')
print(Fore.WHITE + f'length of second list: {len(second_list)}')

# which and how many elements are common to the two lists above? 
def get_common_elements_of_List(list1, list2):
    list1 = set(list1)
    list2 = set(list2)
    return list1.intersection(list2)

print(Fore.BLUE + f'the common elements of the lists are: {get_common_elements_of_List(first_list, second_list)}')

# how often does the character 'a' occur in each of the lists above?
def count_letter_loop(list, letter):
    count = 0
    for elem in list:
        count += elem.count(letter)
    return count

def count_letter_string(list, letter):
    all_letters = ''.join(list)
    return all_letters.count(letter)

print(Fore.LIGHTBLUE_EX + f'there a {count_letter_loop(first_list, "a")} a\'s occuring in the first list ')
print(Fore.LIGHTBLUE_EX + f'there a {count_letter_loop(second_list, "a")} a\'s occuring in the second list ')

## just for fun test cases
def test_get_qoutient_and_remainder_int():
    assert get_qoutient_and_remainder_int(5,2) == (2,1)

def test_fahrenheit_to_celsius():
    assert fahrenheit_to_celsius(32) == 0

def test_count_paranthesis_diff():
    assert count_paranthesis_diff("((())") == "there are 1 more open paranthesis then closed ones"

def test_get_common_elements_of_List():
    list1 = ["abc","cde","FGE"]
    list2 = ["cde", "abc", "ABC"]
    assert get_common_elements_of_List(list1,list2) == {"abc","cde"}  

def test_count_letter_loop():
    test_list = ["abc", "bac", "haha","bde"]
    assert count_letter_loop(test_list, "a") == 4

def test_count_letter_string():
    test_list = ["abc", "bac", "haha","bde"]
    assert count_letter_string(test_list,"a") == 4